package tiralabra;

import tiralabra.benchmark.BenchmarkRunner;
import java.io.IOException;
import java.nio.file.Paths;

public class App {

    public static void main(String[] args) throws IOException {
        if (args.length < 1) {
            System.out.println("Argument for benchmark data location is required");
            System.exit(0);
        }
        BenchmarkRunner.runBencmark(Paths.get(args[0]));
    }

}
